import './App.css';

class App extends Component {
  constructor(props){
    super(props)
        this.state = {
            data: {},
            isLoaded: false,
            amount: 500,
            months: 6
        }
}

onAmountChange = amount => {
  this.setState({ 
        amount 
    });
}

onMonthChange = e => {
  this.setState({ 
    months: + e.target.value 
  }); // + will convert variable to integer
}

getDetails = e => {
  e.preventDefault();
  const url = `https://ftl-frontend-test.herokuapp.com/interest?amount=${this.state.amount}&numMonths=${this.state.months}`;
  axios.get(url)
    .then(res => {
      if (res && res.status === 200) {
        this.setState({
          data: res.data,
          isLoaded: true,
        })
      }
    })
    .catch(err => {
      message.error(err.message);
    });
}


  render() {
    return (
      <div>
